import { promises } from "fs";
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { resolve } from "uri-js";
import { procesador } from "../../Json/procesador.json";

const Itremdelail = () => {
  const arrayProducts = procesador;
  const [viewProduct, setViewProduct] = useState({});
  const { id: idParams } = useParams();
  const getSingleProduct = () => {
    return new Promise((resolve) => {
      setTimeout(() => {
        resolve(arrayProducts.find((item) => item.id.toString()===idParams));
      }, 2000);
    });
  };
  useEffect(() => {
      setViewProduct()
    getSingleProduct().then((resultado) => setViewProduct(resultado));
  }, [idParams]);

  console.log(1234,viewProduct)
  return (
    <div>
      <div className="procesadoritems">
        <img src={viewProduct.img} alt="imagen del producto" className="imgProduct" />
        <h2 className="titulodelProducto">{viewProduct.title} </h2>
        <p className="prices">{viewProduct.price}</p>
        <button className="botonPoducto">ver en detalle</button>
      </div>
    </div>
  );
};
export default Itremdelail;
